{* this section shows a clickable list of News categories. *}
{*&lt;ul class=&quot;list1&quot;&gt;
  {foreach $cats as $node}
  {if $node.depth &gt; $node.prevdepth}
  {repeat string=&quot;&lt;ul&gt;&quot; times=$node.depth-$node.prevdepth}
    {elseif $node.depth &lt; $node.prevdepth} {repeat string=&quot;&lt;/li&gt;&lt;/ul&gt;&quot; times=$node.prevdepth-$node.depth} &lt;/li&gt;
      {elseif $node.index &gt; 0}
      &lt;/li&gt;
      {/if}
      &lt;li{if $node.index==0} class=&quot;firstnewscat&quot; {/if}&gt;
        {if $node.count &gt; 0}
        &lt;a href=&quot;{$node.url}&quot;&gt;{$node.news_category_name}&lt;/a&gt;
        {else}
        &lt;span&gt;{$node.news_category_name} &lt;/span&gt;
        {/if}
        {/foreach}
        {repeat string=&quot;&lt;/li&gt;
  &lt;/ul&gt;&quot; times=$node.depth-1}&lt;/li&gt;
&lt;/ul&gt;*}

{* this displays the category name if you&apos;re browsing by category *}
{*{if $category_name}
&lt;h1&gt;{$category_name}&lt;/h1&gt;
{/if}*}

{* to omit category-browsing from the summary page, remove this line and everything above it *}

{* pressroom-module apparatus ?
{if $pagecount &gt; 1 &amp;&amp; $actionparams.pagination != 0}
&lt;div class=&quot;pagination&quot;&gt;
  {if $pagenumber &gt; 1}
  {$firstpage}&amp;nbsp;{$prevpage}&amp;nbsp;
  {/if}
  {$pagetext}&amp;nbsp;{$pagenumber}&amp;nbsp;{$oftext}&amp;nbsp;{$pagecount}
  {if $pagenumber &lt; $pagecount} &amp;nbsp;{$nextpage}&amp;nbsp;{$lastpage} {/if}
&lt;/div&gt;
{/if} *}
{$readmore=$mod-&gt;Lang(&apos;moreprompt&apos;)}
{foreach $items as $entry}
  &lt;article class=&quot;newssummary&quot;&gt;
    {if !empty($entry-&gt;image)}
     &lt;img class=&quot;image&quot; src=&quot;{$entry-&gt;image}&quot; alt=&quot;{$entry-&gt;imagealt|default:&apos;Related image&apos;}&quot; /&gt;
    {/if}
    &lt;div&gt;
     &lt;a class=&quot;itemtitle&quot; href=&quot;{$entry-&gt;moreurl}&quot; title=&quot;{$entry-&gt;title|cms_escape:htmlall}&quot;&gt;{$entry-&gt;title|cms_escape}&lt;/a&gt;
     &lt;div class=&quot;post-meta&quot;&gt;
      {if $entry-&gt;startdate}
        &lt;p class=&quot;date&quot;&gt;{$entry-&gt;startdate}&lt;/p&gt;
      {/if}
      {if $entry-&gt;author}
        &lt;div class=&quot;author&quot;&gt;
          {$author_label} {$entry-&gt;author}
        &lt;/div&gt;
      {/if}
